mapper.select:
SELECT id, category_id, diary_id, weight, count, `set`, posture_img, done
FROM exercise
WHERE diary_id = #{diaryId};

SELECT id, category_id, diary_id, weight, count, `set`, posture_img, done
FROM exercise
WHERE id = #{id};

mapper.update:
UPDATE exercise
SET done = 1,
    posture_img = #{postureImg}
WHERE id = #{id};
